import React, { useEffect, useState } from 'react';
import './Banner.css';

const Banner = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Add the animate class after a short delay to trigger the fade-in effect
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 500);

    return () => {
      // Add an exit animation when the component unmounts
      setIsVisible(false);
    };
  }, []);

  return (
    <section className={`banner text-center text-white ${isVisible ? 'animate' : ''}`}>
      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <h1 className="display-4">Welcome to Esalink</h1>
            <p className="lead">Your trusted source for innovative solutions</p>
            <button className="btn btn-primary btn-lg">Learn More</button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
